// N8N Code Node to parse Amazon Orders TSV data
// Assumes the raw TSV data is in items[0].json.data or items[0].binary.data

// Get the raw data (adjust path based on your HTTP Request node output)
let rawData;
if (items[0].binary && items[0].binary.data) {
  // If data comes as binary from HTTP Request
  rawData = Buffer.from(items[0].binary.data.data, 'base64').toString('utf-8');
} else if (items[0].json.data) {
  // If data comes as string
  rawData = items[0].json.data;
} else {
  // If the entire response is the data
  rawData = JSON.stringify(items[0].json);
}

// Split into lines and get headers
const lines = rawData.trim().split('\n');
const headers = lines[0].split('\t');

// Parse each data row (skip header row)
const orders = [];
for (let i = 1; i < lines.length; i++) {
  if (lines[i].trim() === '') continue; // Skip empty lines

  const values = lines[i].split('\t');

  // Skip invalid orders (sample data, malformed, etc.)
  const amazonOrderId = values[0] || '';
  if (amazonOrderId.startsWith('S01-') || amazonOrderId.trim() === '' || values.length < 10) {
    continue; // Skip this row
  }

  const order = {};

  // Map each value to its corresponding header
  headers.forEach((header, index) => {
    let value = values[index] || '';

    // Clean up common fields and convert types
    switch(header) {
      case 'quantity':
        order[header] = parseInt(value) || 0;
        break;
      case 'item-price':
      case 'item-tax':
      case 'shipping-price':
      case 'shipping-tax':
      case 'gift-wrap-price':
      case 'gift-wrap-tax':
      case 'item-promotion-discount':
      case 'ship-promotion-discount':
        order[header] = parseFloat(value) || 0;
        break;
      case 'purchase-date':
      case 'last-updated-date':
        order[header] = value ? new Date(value).toISOString() : null;
        break;
      case 'is-business-order':
      case 'signature-confirmation-recommended':
        order[header] = value === 'true';
        break;
      default:
        order[header] = value;
    }
  });

  // Add some calculated fields
  order.total_price = (order['item-price'] || 0) + (order['item-tax'] || 0) + 
                     (order['shipping-price'] || 0) + (order['shipping-tax'] || 0);

  orders.push(order);
}

// Return array of order objects
return orders.map(order => ({
  json: order
}));

