    });
  }
}

return updates;

const keywords = $node["Loop Over Keywords"].json;
const keywordArray = Array.isArray(keywords) ? keywords : [keywords];

// Group keywords by ad group
const keywordsByAdGroup = {};
keywordArray.forEach(keyword => {
  const adGroupId = keyword.adGroupId.toString();
  if (!keywordsByAdGroup[adGroupId]) {
    keywordsByAdGroup[adGroupId] = {
      adGroupId: adGroupId,
      campaignId: keyword.campaignId.toString(),
      keywords: []
    };
  }
  keywordsByAdGroup[adGroupId].keywords.push(keyword);
});

// Create V3 API requests for each ad group in the exact format Amazon expects
const requests = Object.values(keywordsByAdGroup).map(adGroup => {

  // Build targeting expressions array
  const targetingExpressions = adGroup.keywords.map(keyword => {
    let matchType = 'KEYWORD_BROAD_MATCH'; // default

    switch(keyword.matchType?.toLowerCase()) {
      case 'exact':
        matchType = 'KEYWORD_EXACT_MATCH';
        break;
      case 'phrase':
        matchType = 'KEYWORD_PHRASE_MATCH';
        break;
      case 'broad':
      default:
        matchType = 'KEYWORD_BROAD_MATCH';
        break;
    }

    return {
      type: matchType,
      value: keyword.keywordText
    };
  });

  // Return exact format Amazon expects
  return {
    json: {
      recommendationType: "BIDS_FOR_EXISTING_AD_GROUP",
      targetingExpressions: targetingExpressions.slice(0, 100), // Max 100 per request
      campaignId: adGroup.campaignId,
      adGroupId: adGroup.adGroupId
    }
  };
});

console.log(`Created ${requests.length} V3 bid recommendation requests`);
console.log('Sample request format:', JSON.stringify(requests[0]?.json, null, 2));

return requests;

