// n8n Code Node - Simple Instagram Post Parser
// Extracts only: username, caption, image URL, post URL, and likes count

const inputData = $input.all();
const posts = [];

// Process each item in the input data
for (const item of inputData) {
  const data = item.json;

  // Handle both array and single object inputs
  const postsArray = Array.isArray(data) ? data : [data];

  for (const post of postsArray) {
    // Get all image URLs - prioritize the images array, fallback to displayUrl
    let allImages = [];

    if (post.images && post.images.length > 0) {
      // Use the images array if available (contains all images for carousels)
      allImages = post.images;
    } else if (post.displayUrl) {
      // Fallback to single displayUrl for single image posts
      allImages = [post.displayUrl];
    }

    const parsedPost = {
      username: post.ownerUsername,
      caption: post.caption,
      imageUrl: post.displayUrl, // Keep the main/first image
      allImageUrls: allImages, // All images in the post
      imageCount: allImages.length, // Number of images
      postUrl: post.url,
      likesCount: post.likesCount
    };

    posts.push(parsedPost);
  }
}

// Return the parsed posts as separate items
return posts.map(post => ({ json: post }));

