const content = $json.choices[0].message.content;

const titleMatch = content.match(/Song Title:\s*(.+)/i);
const artistMatch = content.match(/Artist:\s*(.+)/i);
const timeMatch = content.match(/Timestamp:\s*(\d+)/i);

return [
  {
    json: {
      songTitle: titleMatch ? titleMatch[1].trim() : "Unknown Title",
      artist: artistMatch ? artistMatch[1].trim() : "Unknown Artist",
      timestamp: timeMatch ? parseInt(timeMatch[1].trim()) : 0
    }
  }
];

let overlayBinary = null;
let clipBinary = null;

for (const item of items) {
  const binaryKeys = Object.keys(item.binary || {});

  for (const key of binaryKeys) {
    const file = item.binary[key];
    const fileName = file.fileName || "";

    if (fileName.endsWith(".mp4")) {
      clipBinary = file;
    } else if (fileName.endsWith(".png")) {
      overlayBinary = file;
    }
  }
}

if (!clipBinary || !overlayBinary) {
  throw new Error("Missing .mp4 or .png file in input");
}

return [
  {
    binary: {
      clip: clipBinary,
      overlay: overlayBinary
    }
  }
];

