const content = $json.choices[0].message.content;

const titleMatch = content.match(/Title:\s*(.+)/);
const descMatch = content.match(/Description:\s*([\s\S]*)/);

return [
  {
    json: {
      gptTitle: titleMatch ? titleMatch[1].trim() : "Untitled",
      gptDescription: descMatch ? descMatch[1].trim() : "No description",
    }
  }
];

// This node runs once, before the loop
const anchor = new Date(); 

return [
  {
    json: {
      anchorTime: anchor.toISOString()
    }
  }
];

const output = $json["stdout"];
if (!output) {
  throw new Error("No stdout output from FFprobe.");
}

const [widthStr, heightStr] = output.trim().split(',');

const width = parseInt(widthStr);
const height = parseInt(heightStr);

if (isNaN(width) || isNaN(height)) {
  throw new Error("Failed to parse image width and height.");
}

return [
  {
    json: {
      width,
      height,
      aspectRatio: width / height
    }
  }
];

const axios = require('axios');

const postData = $items('Get URL3')[0].json;
const openaiKey = $env.OPENAI_API_KEY;

if (postData.error) {
  console.log("‚è≠Ô∏è Skipping GPT generation due to previous error");
  return [
    {
      json: {
        reply: "Error: Could not generate reply",
        error: true,
        originalError: postData.errorMessage
      }
    }
  ];
}

// Handle different input formats to get a single image URL
let imageUrl = null;

// Check if postData is a string (direct URL)
if (typeof postData === 'string') {
  imageUrl = postData;
}
// Check if postData has a url property
else if (postData.url) {
  imageUrl = postData.url;
}
// Check if postData has an array and take the first item
else if (postData.files && Array.isArray(postData.files) && postData.files.length > 0) {
  imageUrl = postData.files[0].url || postData.files[0];
}
// Check for imageUrls array (legacy format) and take first
else if (postData.imageUrls && Array.isArray(postData.imageUrls) && postData.imageUrls.length > 0) {
  imageUrl = postData.imageUrls[0];
}

console.log(`ü§ñ Processing single Facebook post image`);

// Validate and convert Dropbox URL if needed
if (!imageUrl || imageUrl.includes('placeholder')) {
  console.log("‚ùå No valid image URL found");
  return [
    {
      json: {
        reply: "Error: No valid image found",
        error: true,
        errorMessage: "No valid image URL provided"
      }
    }
  ];
}

// Convert Dropbox share links to direct image URLs if needed
if (imageUrl.includes('dropbox.com')) {
  if (imageUrl.includes('?dl=0')) {
    imageUrl = imageUrl.replace('?dl=0', '?raw=1');
  }
  if (imageUrl.includes('&dl=0')) {
    imageUrl = imageUrl.replace('&dl=0', '&raw=1');
  }
}

console.log(`üì∏ Processing image URL: ${imageUrl.substring(0, 50)}...`);

// Create image input for GPT Vision
const imageInput = {
  type: "image_url",
  image_url: {
    url: imageUrl,
    detail: "low"
  }
};

// Messages for reply generation
const replyMessages = [
  {
    role: "user",
    content: [
      {
        type: "text",
        text: `Look at this Facebook post image and reply as if you're a real person scrolling social media, not a bot. Your tone should be chill, observant, and down-to-earth, like someone casually commenting something thoughtful, lighthearted, or insightful without trying too hard. Don't repeat the fact from the post. No emojis. No hashtags. No em dashes or en dashes. Keep it under 150 characters.`
      },
      imageInput
    ]
  }
];

try {
  // Generate reply
  console.log("üí¨ Generating reply...");
  const replyResponse = await axios.post(
    "https://api.openai.com/v1/chat/completions",
    {
      model: "gpt-4o",
      messages: replyMessages,
      temperature: 0.8,
      max_tokens: 150
    },
    {
      headers: {
        Authorization: `Bearer ${openaiKey}`,
        "Content-Type": "application/json"
      }
    }
  );

  let reply = replyResponse.data.choices[0].message.content.trim();

  // Clean up quotes if present
  if ((reply.startsWith('"') && reply.endsWith('"')) ||
      (reply.startsWith("'") && reply.endsWith("'"))) {
    reply = reply.slice(1, -1).trim();
  }

  console.log(`‚úÖ Generated reply: "${reply}"`);

  return [
    {
      json: {
        reply,
        usedVision: true,
        model: "gpt-4o",
        imageProcessed: imageUrl.substring(0, 100) + "..."
      }
    }
  ];

} catch (error) {
  console.error(`‚ùå Error generating GPT reply:`, error.message);

  // Log more details about the error
  if (error.response) {
    console.error(`üìã Response status: ${error.response.status}`);
    console.error(`üìã Response data:`, JSON.stringify(error.response.data, null, 2));
  }

  return [
    {
      json: {
        reply: "sorry, my brain broke trying to think of a reply ü§ñüí•",
        error: true,
        errorMessage: error.message,
        debugInfo: error.response ? {
          status: error.response.status,
          data: error.response.data
        } : null
      }
    }
  ];
}

const axios = require('axios');
const gptData = $items('Chat Reply2')[0].json;  // Updated node: gpt reply
const cropData = $items('Get URL3')[0].json;  // Updated node: get crop url
const ratioData = $items('Aspect Ratio')[0].json;  // Fixed: added $ prefix
const templatedKey = $env.TEMPLATED_API_KEY;

console.log(`üñºÔ∏è Generating Templated.io image...`);
console.log(`üìê Aspect ratio: ${ratioData.aspectRatio}`);

async function waitForTemplateImage(id, apiKey) {
  const maxAttempts = 10;
  const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    console.log(`‚åõ Waiting for image (attempt ${attempt})...`);
    const statusRes = await axios.get(`https://api.templated.io/v1/render/${id}`, {
      headers: { Authorization: `Bearer ${apiKey}` }
    });

    if (statusRes.data.url) {
      return statusRes.data.url;
    }
    await delay(2000); // wait 2 seconds
  }
  throw new Error("‚ùå Timeout: Template image rendering took too long.");
}

try {
  // Select template based on aspect ratio
  const aspectRatio = ratioData.aspectRatio;
  let selectedTemplate;

  if (aspectRatio <= 0.9) {
    selectedTemplate = "f7164bd0-3376-4d64-8563-95964913223d";
    console.log(`üì± Using portrait/square template (ratio: ${aspectRatio})`);
  } else {
    selectedTemplate = "2d4237a3-b0cd-41c4-a436-8e8ce3875c4b";
    console.log(`üñ•Ô∏è Using landscape template (ratio: ${aspectRatio})`);
  }

  const layers = {
    reply: {
      text: gptData.reply
    },
    image: {
      image_url: cropData.url.replace('dl=0', 'dl=1')
    }
  };

  const response = await axios.post(
    "https://api.templated.io/v1/render",
    {
      template: selectedTemplate,
      layers,
      format: "png",
      async: false
    },
    {
      headers: {
        Authorization: `Bearer ${templatedKey}`,
        "Content-Type": "application/json"
      }
    }
  );

  let finalImageUrl;
  if (response.data.url) {
    finalImageUrl = response.data.url;
    console.log(`‚úÖ Templated image ready instantly: ${finalImageUrl}`);
  } else if (response.data.id) {
    finalImageUrl = await waitForTemplateImage(response.data.id, templatedKey);
    console.log(`‚úÖ Templated image ready after wait: ${finalImageUrl}`);
  } else {
    throw new Error("No image URL or template ID returned.");
  }

  return [
    {
      json: {
        generatedImageUrl: finalImageUrl,
        templateId: response.data.id || 'instant',
        selectedTemplate: selectedTemplate,
        aspectRatio: aspectRatio,
        usedVision: !!cropData.link && !cropData.link.includes('placeholder'),
        success: true
      }
    }
  ];
} catch (error) {
  console.error("‚ùå Error during template image generation:", error.message);
  return [
    {
      json: {
        generatedImageUrl: null,
        error: true,
        errorMessage: error.message
      }
    }
  ];
}
